import{todos,updateIndexes}from"./todo";function updateTaskCompletion(o,t){const e=todos.find((t=>t.index===o));e&&(e.completed=t,localStorage.setItem("tasks",JSON.stringify(todos)))}function clearCompleted(){const o=todos.filter((o=>!o.completed));todos.length=0,Array.prototype.push.apply(todos,o),updateIndexes(),localStorage.setItem("tasks",JSON.stringify(todos))}export{todos,updateTaskCompletion,clearCompleted};